app.directive('customNgModel',['$timeout', function($timeout) {
    return {
        restrict: 'A',
        scope: {
            model: "="
        },
        link: function(scope, element) {
            $timeout(function() {
                $(element).on('input', function() {
                    scope.model = $(this).val();
                    if (scope.$$phase != '$apply' && scope.$$phase != '$digest') {
                        scope.$apply();
                    }
                })
            });
            scope.$watch('model', function(val) {
                    $(element).val(val);
            })
        }
    };
}]);

